/**
 *  Alert
 */
function alertSend(content, auto) {
    $(".alert-content").text(content);
    $(".alert").removeClass("ready");
    if (auto === true) {
        window.setTimeout(function() {
            $(".alert").addClass("ready");
        }, 2000);
    }
}

function alertClose() {
    $(".alert").addClass("ready");
}

