/**
 *  Alert
 */
function alertSend(content, type) {
	$("body").append("<div class=\"alert adding " + type + "\">"+ content +"</div>");
    window.setTimeout(function() {
		$(".alert").removeClass("adding");
        $(".alert").addClass("remove");
		window.setTimeout(function() {
			$(".alert").remove();
		}, 200);
    }, 2500);
}

